/**
 * Markdown 格式工具集
 * 提供文本格式检测、添加/移除格式等功能
 */

/**
 * 检测文本中应用的Markdown格式
 * @param {string} text 要检测的文本
 * @returns {Object} 格式状态对象
 */
export function detectFormats(text) {
  if (!text || typeof text !== 'string') return {}

  return {
    bold: isWrappedWith(text, '**'),
    italic: isWrappedWith(text, '_') || isWrappedWith(text, '*'),
    h1: text.startsWith('# '),
    h2: text.startsWith('## '),
    h3: text.startsWith('### '),
    ul: text.startsWith('- ') || text.startsWith('* '),
    ol: /^\d+\. /.test(text),
    code: isWrappedWith(text, '```\n', '\n```') || isWrappedWith(text, '`'),
    quote: text.startsWith('> ')
  }
}

/**
 * 检查文本是否被指定字符包裹
 * @param {string} text 要检查的文本
 * @param {string} prefix 前缀
 * @param {string} [suffix] 后缀（默认同前缀）
 * @returns {boolean}
 */
function isWrappedWith(text, prefix, suffix = prefix) {
  return text.startsWith(prefix) && text.endsWith(suffix)
}

/**
 * 切换Markdown格式（添加/移除）
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} prefix 格式前缀
 * @param {string} suffix 格式后缀
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function toggleMarkdownFormat(content, selection, prefix, suffix) {
  const { start, end, text } = selection

  // 检查是否已应用格式
  const isFormatted = isWrappedWith(text, prefix, suffix)

  if (isFormatted) {
    return removeMarkdownFormat(content, selection, prefix, suffix)
  } else {
    return addMarkdownFormat(content, selection, prefix, suffix)
  }
}

/**
 * 添加Markdown格式
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} prefix 格式前缀
 * @param {string} suffix 格式后缀
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function addMarkdownFormat(content, selection, prefix, suffix) {
  const { start, end, text } = selection

  return {
    content: content.slice(0, start) + 
             prefix + text + suffix + 
             content.slice(end),
    newStart: start + prefix.length,
    newEnd: end + prefix.length
  }
}

/**
 * 移除Markdown格式
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} prefix 格式前缀
 * @param {string} suffix 格式后缀
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function removeMarkdownFormat(content, selection, prefix, suffix) {
  const { start, end, text } = selection

  return {
    content: content.slice(0, start) + 
             text.slice(prefix.length, -suffix.length) + 
             content.slice(end),
    newStart: start,
    newEnd: end - prefix.length - suffix.length
  }
}

/**
 * 插入Markdown链接
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} linkText 链接文字
 * @param {string} url 链接地址
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function insertMarkdownLink(content, selection, linkText, url) {
  const { start, end } = selection
  const markdownLink = `[${linkText}](${url})`

  return {
    content: content.slice(0, start) + 
             markdownLink + 
             content.slice(end),
    newStart: start + markdownLink.length,
    newEnd: start + markdownLink.length
  }
}

/**
 * 插入Markdown图片
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} altText 替代文字
 * @param {string} url 图片地址
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function insertMarkdownImage(content, selection, altText, url) {
  const { start, end } = selection
  const markdownImage = `![${altText}](${url})`

  return {
    content: content.slice(0, start) + 
             markdownImage + 
             content.slice(end),
    newStart: start + markdownImage.length,
    newEnd: start + markdownImage.length
  }
}

/**
 * 智能处理列表格式
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} type 列表类型 ('ul' 或 'ol')
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
// 更新handleListFormat方法
export function handleListFormat(content, selection, type = 'ul') {
  const { start, end, text } = selection;
  const lines = text.split('\n');

  // 检查是否已经是列表
  const isAlreadyList = lines.every(line => 
    type === 'ul' 
      ? line.startsWith('- ') || line.startsWith('* ')
      : /^\d+\. /.test(line)
  );

  if (isAlreadyList) {
    // 移除列表格式
    const newLines = lines.map(line => 
      type === 'ul' 
        ? line.replace(/^[-*] /, '') 
        : line.replace(/^\d+\. /, '')
    );
    const newText = newLines.join('\n');
    return {
      content: content.slice(0, start) + newText + content.slice(end),
      newStart: start,
      newEnd: start + newText.length
    };
  } else {
    // 添加列表格式
    const newLines = lines.map((line, i) => 
      type === 'ul' 
        ? `- ${line}` 
        : `${i + 1}. ${line}`
    );
    const newText = newLines.join('\n');
    const lengthDiff = newText.length - text.length;
    return {
      content: content.slice(0, start) + newText + content.slice(end),
      newStart: start,
      newEnd: end + lengthDiff
    };
  }
}



/**
 * 处理多行格式（如代码块、引用）
 * @param {string} content 完整内容
 * @param {Object} selection 选区对象 { start, end, text }
 * @param {string} prefix 每行前缀
 * @param {string} [suffix] 每行后缀（可选）
 * @returns {Object} 结果对象 { content, newStart, newEnd }
 */
export function handleMultilineFormat(content, selection, prefix, suffix = '') {
  const { start, end, text } = selection
  const lines = text.split('\n')

  // 检查是否已经应用格式
  const isFormatted = lines.every(line => 
    line.startsWith(prefix) && (suffix ? line.endsWith(suffix) : true)
  )

  if (isFormatted) {
    // 移除格式
    const newLines = lines.map(line => 
      suffix 
        ? line.slice(prefix.length, -suffix.length)
        : line.slice(prefix.length)
    )
    const newText = newLines.join('\n')

    return {
      content: content.slice(0, start) + newText + content.slice(end),
      newStart: start,
      newEnd: start + newText.length
    }
  } else {
    // 添加格式
    const newLines = lines.map(line => prefix + line + suffix)
    const newText = newLines.join('\n')
    const lengthDiff = newText.length - text.length

    return {
      content: content.slice(0, start) + newText + content.slice(end),
      newStart: start,
      newEnd: end + lengthDiff
    }
  }
}

// 常用格式的快捷方法
export const formatHelpers = {
  bold: (content, selection) => 
    toggleMarkdownFormat(content, selection, '**', '**'),

  italic: (content, selection) => 
    toggleMarkdownFormat(content, selection, '_', '_'),

  h1: (content, selection) => 
    toggleMarkdownFormat(content, selection, '# ', ''),

  h2: (content, selection) => 
    toggleMarkdownFormat(content, selection, '## ', ''),

  h3: (content, selection) => 
    toggleMarkdownFormat(content, selection, '### ', ''),

  ul: (content, selection) => 
    handleListFormat(content, selection, 'ul'),

  ol: (content, selection) => 
    handleListFormat(content, selection, 'ol'),

  code: (content, selection) => 
    handleMultilineFormat(content, selection, '```\n', '\n```'),

  quote: (content, selection) => 
    handleMultilineFormat(content, selection, '> ')
}
